mapper.select:
SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
and o.status = #{filteType}
and o.stage = 1
and o.stage = 3
and o.status > 1
AND (o.content like CONCAT('%', '${key}' ,'%') or o.label like CONCAT('%', '${key}' ,'%'))
AND (o.content like CONCAT('%', '${key}' ,'%'))
AND (o.label like CONCAT('%', '${key}' ,'%'))
and o.uid = #{uid}
and o.sid = #{sid}
and o.pid = #{pid}
and s.pid = #{parkId}

SELECT o.*,p.pid as ppid,p.name as pname, p.phoneNum as pphoneNum, p.contact as ppcontact, u.uid as uuid, u.name as uname,u.phoneNum as uphoneNum, u.companyName as ucompanyName FROM psp_order o left join psp_provider p on o.pid = p.pid left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
and o.status = #{filteType}
and o.stage = 1
and o.stage = 3
and o.status > 1
AND (o.content like CONCAT('%', '${key}' ,'%') or o.label like CONCAT('%', '${key}' ,'%'))
AND (o.content like CONCAT('%', '${key}' ,'%'))
AND (o.label like CONCAT('%', '${key}' ,'%'))
and o.uid = #{uid}
and o.sid = #{sid}
and o.pid = #{pid}
and s.pid = #{parkId}
ORDER BY o.stage, o.updateTime desc, o.createTime desc LIMIT #{start},#{length}

SELECT (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 0
and s.pid = #{parkId}
) as toAllot, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 2
and s.pid = #{parkId}
) as pending, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 3
and s.pid = #{parkId}
) as accept, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 4
and s.pid = #{parkId}
) as toContract, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 5
and s.pid = #{parkId}
) as toApplyCompelete, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 6
and s.pid = #{parkId}
) as toFeedback, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 7
and s.pid = #{parkId}
) as refuse, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 8
and s.pid = #{parkId}
) as toApplyFinished, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status = 1
and s.pid = #{parkId}
) as completed, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.status < 0
and s.pid = #{parkId}
) as closed from dual;

SELECT (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.stage = 1
and s.pid = #{parkId}
) as underway, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.stage = 2
and s.pid = #{parkId}
) as completed, (SELECT count(*) FROM psp_order o left join psp_user u on o.uid = u.uid left join psp_sale_center s on u.sid = s.sid
o.stage = 3
and s.pid = #{parkId}
) as closed from dual;
