mapper.select:
SELECT count(*) FROM psp_provider p
LEFT JOIN psp_provider_category_relation r ON p.pid = r.pid
r.cid = #{r.cid}

SELECT * FROM psp_provider p
LEFT JOIN psp_provider_category_relation r ON p.pid = r.pid
r.cid = #{r.cid}
order by p.status, p.createTime DESC LIMIT #{start},#{length}

SELECT * FROM psp_provider_account
pid = #{pid}
order by status, createTime DESC LIMIT #{start},#{length}
