mapper.select:
SELECT count(*) 
FROM psp_user

	sid = #{sid}
	
	
		and level = #{filteType}
	
	
	
		AND (name like CONCAT('%', '${key}' ,'%'))
	
	
		AND (phoneNum like CONCAT('%', '${key}' ,'%')) 
	
	
		AND (companyName like CONCAT('%', '${key}' ,'%')) 
	
	
	
		and status = #{status}

SELECT * FROM psp_user 

	sid = #{sid}
	
	
		and level = #{filteType}
	
	
	
		AND (name like CONCAT('%', '${key}' ,'%'))
	
	
		AND (phoneNum like CONCAT('%', '${key}' ,'%')) 
	
	
		AND (companyName like CONCAT('%', '${key}' ,'%')) 
	
	
	
		and status = #{status}
	
 
ORDER BY isAllot Desc, createTime desc
LIMIT #{start},#{length}

mapper.update:
UPDATE psp_user 
SET 
	sid = #{sid},sellerJson = #{sellerJson} 
WHERE
	uid = #{uid};

UPDATE psp_user
name=#{name}, phoneNum=#{phoneNum}, companyName=#{companyName}, position=#{position}, label=#{label},
WHERE uid = #{uid};

UPDATE psp_user 
SET 
	level = #{level}
WHERE
	uid = #{uid};

UPDATE psp_user 
SET 
	label = #{label}
WHERE
	uid = #{uid};

UPDATE psp_user 
SET 
	status = #{status}
WHERE
	uid = #{uid};
