mapper.select:
SELECT count(*) FROM psp_user u left join psp_sale_center s on u.sid = s.sid
and u.level = #{filteType}
AND (u.name like CONCAT('%', '${key}' ,'%') or u.phoneNum like CONCAT('%', '${key}' ,'%') or u.companyName like CONCAT('%', '${key}' ,'%'))
AND (u.name like CONCAT('%', '${key}' ,'%'))
AND (u.phoneNum like CONCAT('%', '${key}' ,'%'))
AND (u.companyName like CONCAT('%', '${key}' ,'%'))
and u.isALlot = 1
and u.isALlot = 0
and u.sid = #{sid}
and s.pid = #{parkId}

SELECT u.*, s.username as susername, s.phoneNum as sphoneNum FROM psp_user u left join psp_sale_center s on u.sid = s.sid
and u.level = #{filteType}
AND (u.name like CONCAT('%', '${key}' ,'%') or u.phoneNum like CONCAT('%', '${key}' ,'%') or u.companyName like CONCAT('%', '${key}' ,'%'))
AND (u.name like CONCAT('%', '${key}' ,'%'))
AND (u.phoneNum like CONCAT('%', '${key}' ,'%'))
AND (u.companyName like CONCAT('%', '${key}' ,'%'))
and u.isALlot = 1
and u.isALlot = 0
and u.sid = #{sid}
and s.pid = #{parkId}
ORDER BY u.isAllot, u.createTime desc LIMIT #{start},#{length}

SELECT
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.level = 1
			
			and s.pid = #{parkId}
			
		
	) as valid,
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.level = 2
			
			and s.pid = #{parkId}
			
		
	) as unvalid,
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.level = 3
			
			and s.pid = #{parkId}
			
		
	) as unrated
from dual;

SELECT
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.isAllot = 1
			
			and s.pid = #{parkId}
			
		
	) as communicate,
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.isAllot = 0
			
			and s.pid = #{parkId}
			
		
	) as uncommunicate
from dual;

SELECT
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.origin = 0
			
			and s.pid = #{parkId}
			
		
	) as online,
	(SELECT count(*) 
		FROM psp_user u
		left join psp_sale_center s
		on u.sid = s.sid
		
			u.origin = 2
			
			and s.pid = #{parkId}
			
		
	) as offline
from dual;
