mapper.select:
SELECT count(*) FROM psp_order
pid = #{pid}
and status = #{filteType}
and status > 1
AND (content like CONCAT('%', '${key}' ,'%'))
AND (label like CONCAT('%', '${key}' ,'%'))

SELECT o.*, p.pid as ppid,p.name as pname, p.phoneNum as pphoneNum, p.contact as ppcontact, s.sid as ssid,s.username as sname, s.phoneNum as sphoneNum, u.uid as uuid,u.name as uname, u.phoneNum as uphoneNum FROM psp_order o left join psp_provider p on o.pid = p.pid left join psp_sale_center s on o.sid = s.sid left join psp_user u on o.uid = u.uid
o.pid = #{pid}
and o.status = #{filteType}
and o.status > 1
AND (o.content like CONCAT('%', '${key}' ,'%'))
AND (o.label like CONCAT('%', '${key}' ,'%'))
ORDER BY o.stage, o.updateTime desc, o.createTime desc LIMIT #{start},#{length}

SELECT count(*) 
FROM psp_order

	pid = #{pid}
	
	
		and stage = #{stage}
