mapper.select:
SELECT u.*, s.username as susername, s.phoneNum as sphoneNum FROM psp_user u
u.sid = #{sid}
and u.level = #{filteType}
AND (u.name like CONCAT('%', '${key}' ,'%'))
AND (u.phoneNum like CONCAT('%', '${key}' ,'%'))
AND (u.companyName like CONCAT('%', '${key}' ,'%'))
and u.status = 0
and u.status = 1
ORDER BY u.createTime desc LIMIT #{start},#{length}

mapper.update:
UPDATE psp_user 
SET 
	sid = #{sid}
WHERE
	uid = #{uid};

UPDATE psp_user
name=#{name}, phoneNum=#{phoneNum}, companyName=#{companyName}, position=#{position}, label=#{label},
WHERE uid = #{uid};

UPDATE psp_user 
SET 
	level = #{level}
WHERE
	uid = #{uid};

UPDATE psp_user 
SET 
	label = #{label}
WHERE
	uid = #{uid};

UPDATE psp_user 
SET 
	status = #{status}
WHERE
	uid = #{uid};
